mapper.select:
SELECT * FROM standard_seminar_system.student
WHERE

    account=#{param1}


    student_name=#{param2}


    account=#{param1} AND student_name=#{param2}

mapper.update:
UPDATE standard_seminar_system.student


        account=#{param2},


        email=#{param3},


        student_name=#{param4},


        sex=#{param5}


WHERE id=#{param1}
